import './Css/Upload.css';
import React, { Component } from 'react';
import Button from '@material-ui/core/Button';


class Upload extends Component {

  storeFile(e){
    var img = document.getElementById("tempImage");
    var selectedFile = document.getElementById("contained-button-file").files;
    if(selectedFile.length > 0){
      if(window.FileReader) {
        console.log(e);
        var file  = e.target.files[0];
        var reader = new FileReader();
        if (file && file.type.match('image.*')) {
          reader.readAsDataURL(file);
        } else {
          img.css('display', 'none');
          img.attr('src', '');
        }
        reader.onloadend = function (e) {
          img.attr('src', reader.result);
          img.css('display', 'block');
        }
      }
    }
  }

  render(){

    return (
    <div className = "upload">

        <h3>Please upload some code or Huw's logo here</h3> <br />

        <input
          type="file"
          accept="image/*"
          style={{ display: 'none' }}
          id="contained-button-file"
        />
        <label htmlFor="contained-button-file">
          <Button variant="contained" color="primary" component="span" onClick={ this.storeFile }>
            Upload
          </Button>
          <img src="" id="tempImage" />
        </label>
      </div>
    );
  }
}

export default Upload;
